import { useContext } from 'react'
import { Button } from '../navbar/Button'
import { Context } from '../../context'

function StepOne() {
    const { inputName, setInputName, inputEmail, setInputEmail, inputPhone, setInputPhone } =
        useContext(Context)

    const handleName = (event) => {
        setInputName(
            event.target.value.replace(/[А-Яа-яёЁ]|[0-9]|[!/?><.,/'";:{}=+-_()&^%$#@*§|~`]/, '')
        )
    }

    const handleEmail = (event) => {
        setInputEmail(event.target.value.replace(/[А-Яа-яёЁ]|[{}%§~`]/, ''))
    }

    const handlePhoneNumber = (event) => {
        let num = event.target.value
        const formatPhoneNumber = (value) => {
            // const regex = /^(\d{0,4})(\d{0,4})(\d{0,4})(\d{0,4})$/g
            const regex = /^[\d]{1}\ \([\d]{2,3}\)\ [\d]{2,3}-[\d]{2,3}-[\d]{2,3}$/
            const onlyNumbers = value.replace(/[^\d]/g, '')

            setInputPhone(
                onlyNumbers.replace(regex, (regex, $1, $2, $3, $4) =>
                    [$1, $2, $3, $4].filter((group) => !!group).join(' ')
                )
            )
        }
        formatPhoneNumber(num)
    }

    // (/\+7\(\d{3}\)\d{3}(-\d{2})\1/, '')
    return (
        <div className="one">
            <div className="step-one">
                <header>
                    <h3>Personal info</h3>
                    <span className="header-span">
                        Please provide your name, email address, and phone number.
                    </span>
                </header>
                <div className="name">
                    <p className="text-name">Name</p>
                    <p className="invalid-text">Please, enter correct value</p>
                    <input
                        value={inputName}
                        className="input-name"
                        placeholder="e.g. Stephen King"
                        onChange={handleName}
                    />
                </div>
                <div className="email">
                    <p className="text-name">Email Address</p>
                    <p className="invalid-text">Please, enter correct value</p>
                    <input
                        value={inputEmail}
                        className="input-email"
                        placeholder="e.g. stephenking@gmail.com"
                        onChange={handleEmail}
                    />
                </div>
                <div className="phone">
                    <p className="text-name">Phone Number</p>
                    <p className="invalid-text">Please, enter correct value</p>
                    <input
                        value={inputPhone}
                        className="input-phonenumber"
                        placeholder="e.g. +1 234 567 890"
                        onChange={handlePhoneNumber}
                    />
                </div>
            </div>
            <Button />
        </div>
    )
}

export { StepOne }
