import { Form } from './componetns/Form'
import { NavBarMobile } from './componetns/navbar/NavBarMobile'
import { NavbarDesktop } from './componetns/navbar/NavbarDesktop'
import { Button } from './componetns/navbar/Button'
import { ContextFunction } from './context'

import { useContext } from 'react'
import { Context } from '../../context'

function App() {
    const { stepOneCompleted, stepTwoCompleted, stepThreeCompleted, stepFourCompleted } =
        useContext(Context)

    let width = window.innerWidth
    if (width < 768) {
        if (stepOneCompleted && stepTwoCompleted && stepThreeCompleted && stepFourCompleted) {
            return (
                <ContextFunction>
                    <main>
                        <NavBarMobile />
                        <Form />
                    </main>
                </ContextFunction>
            )
        } else {
            return (
                <ContextFunction>
                    <main>
                        <NavBarMobile />
                        <Form />
                        <Button />
                    </main>
                </ContextFunction>
            )
        }
    }
    return (
        <ContextFunction>
            <main>
                <NavbarDesktop />
                <Form />
            </main>
        </ContextFunction>
    )
}
export default App
