import { useContext } from 'react'
import { Button } from '../navbar/Button'
import { Context } from '../../context'

function StepOne() {
    const {
        inputName,
        setInputName,
        inputEmail,
        setInputEmail,
        inputPhone,
        setInputPhone,
        isValidName,
        setIsValidName,
        isValidEmail,
        setIsvalidEmail,
        isValidPhoneNumber,
        setIsValidPhoneNumber,
        stepOneCompleted,
        setStepOneCompleted,
    } = useContext(Context)

    const handleName = (event) => {
        setInputName(
            event.target.value.replace(/[А-Яа-яёЁ]|[0-9]|[!/?><.,/'";:{}=+-_()&^%$#@*§|~`]/, '')
        )
    }
    const nameBlur = () => {
        inputName.length < 5 ? setIsValidName(false) : setIsValidName(true)
    }

    const handleEmail = (event) => {
        setInputEmail(event.target.value.replace(/[А-Яа-яёЁ]|[{}%§~`]/, ''))
    }
    const emailBlur = () => {
        inputEmail.length < 12 ? setIsvalidEmail(false) : setIsvalidEmail(true)
    }

    const handlePhoneNumber = (event) => {
        let num = event.target.value
        function phoneNum(num) {
            const regex = /(\d{1})(\d{3})(\d{3})(\d{2})(\d{2})/g
            const onlyNumbers = num.replace(/[^\d]/g, '')
            const subst = '+$1 ($2) $3-$4-$5'

            setInputPhone(onlyNumbers.replace(regex, subst))
        }
        phoneNum(num)
    }
    const phoneBlur = () => {
        inputPhone.length !== 18 ? setIsValidPhoneNumber(false) : setIsValidPhoneNumber(true)
    }

    return (
        <div className="one">
            <div className="step-one">
                <header>
                    <h3>Personal info</h3>
                    <span className="header-span">
                        Please provide your name, email address, and phone number.
                    </span>
                </header>
                <div className="name">
                    <p className="text-name">Name</p>
                    <p className="invalid-text">
                        {isValidName ? '' : 'Please, enter correct value'}
                    </p>
                    <input
                        value={inputName}
                        className={isValidName ? 'input-name' : 'input-name invalid-input'}
                        placeholder="e.g. Stephen King"
                        onChange={handleName}
                        onBlur={nameBlur}
                    />
                </div>
                <div className="email">
                    <p className="text-name">Email Address</p>
                    <p className="invalid-text">
                        {isValidEmail ? '' : 'Please, enter correct value'}
                    </p>
                    <input
                        value={inputEmail}
                        className={isValidEmail ? 'input-email' : 'input-email invalid-input'}
                        placeholder="e.g. stephenking@gmail.com"
                        onChange={handleEmail}
                        onBlur={emailBlur}
                    />
                </div>
                <div className="phone">
                    <p className="text-name">Phone Number</p>
                    <p className="invalid-text">
                        {isValidPhoneNumber ? '' : 'Please, enter correct value'}
                    </p>
                    <input
                        maxLength={11}
                        value={inputPhone}
                        className={
                            isValidPhoneNumber
                                ? 'input-phonenumber'
                                : 'input-phonenumber invalid-input'
                        }
                        placeholder="e.g. 79999999999"
                        onChange={handlePhoneNumber}
                        onBlur={phoneBlur}
                    />
                </div>
            </div>
            <Button />
        </div>
    )
}

export { StepOne }
