import { useContext } from 'react'
import { Context } from '../../context'

function Button() {
    const { step, setStep } = useContext(Context)

    const nextStep = () => {
        setStep(step + 1)
    }
    if (step === 1) {
        return (
            <div className="button">
                <></>
                <button onClick={nextStep} className="next-step">
                    Next Step
                </button>
            </div>
        )
    } else {
        return (
            <div className="button">
                <button className="go-back">Go Back</button>
                <button onClick={nextStep} className="next-step">
                    Next Step
                </button>
            </div>
        )
    }
}

export { Button }
