import { useContext } from 'react'
import { Button } from '../navbar/Button'
import { PlanMonth } from './PlanMonth'
import { PlanYear } from './PlanYear'
import { Context } from '../../context'

function StepTwo() {
    const { month, setMonth } = useContext(Context)

    const checked = (event) => {
        if (localStorage.getItem('isChecked') === 'false') {
            event.target.checked = true
            setMonth(false)
        }
        if (event.target.checked) {
            setMonth(false)
        }
        if (!event.target.checked) {
            setMonth(true)
        }
        localStorage.setItem('isChecked', event.target.checked)
    }

    return (
        <div className="two">
            <div className="step-two">
                <header>
                    <h3>Select your plan</h3>
                    <span className="header-span">
                        You have the option of monthly or yearly billing.
                    </span>
                </header>
                {month ? <PlanMonth /> : <PlanYear />}
                <div className="toggle">
                    <div className="month">Monthly</div>
                    <label className="switch">
                        <input type="checkbox" className="switch__input" onChange={checked} />
                        <span className="switch__slider"></span>
                    </label>
                    <div className="year">Yearly</div>
                </div>
            </div>
            <Button />
        </div>
    )
}

export { StepTwo }
