import { useContext } from 'react'
import { Context } from '../../context'

function App() {
    let width = window.innerWidth
    if (width < 768) {
        if (stepOneCompleted && stepTwoCompleted && stepThreeCompleted && stepFourCompleted) {
            return (
                <ContextFunction>
                    <main>
                        <NavBarMobile />
                        <Form />
                    </main>
                </ContextFunction>
            )
        } else {
            return (
                <ContextFunction>
                    <main>
                        <NavBarMobile />
                        <Form />
                        <Button />
                    </main>
                </ContextFunction>
            )
        }
    } else {
        return (
            <ContextFunction>
                <main>
                    <NavbarDesktop />
                    <Form />
                </main>
            </ContextFunction>
        )
    }
}
export default App
