import { Form } from './componetns/Form'
import { NavBarMobile } from './componetns/navbar/NavBarMobile'
import { NavbarDesktop } from './componetns/navbar/NavbarDesktop'
import { Button } from './componetns/navbar/Button'
import { Context, ContextFunction } from './context'
import { useContext } from 'react'

function App() {
    const { step } = useContext(Context)
    let width = window.innerWidth
    if (width < 768) {
        return (
            <ContextFunction>
                <main>
                    <NavBarMobile />
                    <Form />
                    {step === 5 ? '' : <Button />}
                </main>
            </ContextFunction>
        )
    } else {
        return (
            <ContextFunction>
                <main>
                    <NavbarDesktop />
                    <Form />
                </main>
            </ContextFunction>
        )
    }
}

export default App
