import { useContext } from 'react'
import { Context } from '../../context'

function SettingYear() {
    const {
        setOnlineService,
        setLargerStorage,
        setCustomizableProfile,
        setStepThreeCompleted,
        onlineService,
        largerStorage,
        customizableProfile,
    } = useContext(Context)
    setStepThreeCompleted(true)

    const handleService = (event) => {
        if (event.target.checked) {
            setOnlineService(1)
        } else {
            setOnlineService(0)
        }
    }

    const handleStorage = (event) => {
        if (event.target.checked) {
            setLargerStorage(2)
        } else {
            setLargerStorage(0)
        }
    }

    const handleCustomizable = (event) => {
        if (event.target.checked) {
            setCustomizableProfile(2)
        } else {
            setCustomizableProfile(0)
        }
    }
    return (
        <div className="setting">
            <div>
                <label
                    htmlFor="checkbox1"
                    className={onlineService ? 'online-service plan-checked' : 'online-service'}>
                    <input
                        className="setting-input"
                        type="checkbox"
                        id="checkbox1"
                        checked={onlineService ? 'checked' : ''}
                        onChange={handleService}
                    />
                    <div className="setting-text">
                        <div className="set-text">Online service</div>
                        <div className="set-description">Access to multiplayer games</div>
                    </div>
                    <div className="setting-price">+$10/yr</div>
                </label>
            </div>
            <div>
                <label
                    htmlFor="checkbox2"
                    className={largerStorage ? 'online-service plan-checked' : 'online-service'}>
                    <input
                        className="setting-input"
                        type="checkbox"
                        id="checkbox2"
                        checked={largerStorage ? 'checked' : ''}
                        onChange={handleStorage}
                    />
                    <div className="setting-text">
                        <div className="set-text">Larger storage</div>
                        <div className="set-description">Extra 1TB of cloud save</div>
                    </div>
                    <div className="setting-price">+$20/yr</div>
                </label>
            </div>
            <div>
                <label
                    htmlFor="checkbox3"
                    className={
                        customizableProfile ? 'online-service plan-checked' : 'online-service'
                    }>
                    <input
                        className="setting-input"
                        type="checkbox"
                        id="checkbox3"
                        checked={customizableProfile ? 'checked' : ''}
                        onChange={handleCustomizable}
                    />
                    <div className="setting-text">
                        <div className="set-text">Customizable Profile</div>
                        <div className="set-description">Custom theme on your profile</div>
                    </div>
                    <div className="setting-price">+$20/yr</div>
                </label>
            </div>
        </div>
    )
}

export { SettingYear }
